import { FaRunning } from 'react-icons/fa';
import { MdDirectionsBike } from 'react-icons/md';


const { TabPane } = Tabs;

import LoginForm from './LoginForm';

const { Meta } = Card;

const tabList = [
    {
      key: 'tab1',
      tab: 'tab1',
    },
    {
      key: 'tab2',
      tab: 'tab2',
    },
    {
        key: 'tab3',
        tab: 'tab3',
      },
  ];

  const contentList = {
    tab1: <p><Chart/></p>,
    tab2: <p>content2</p>,
    tab3: <p>content3</p>,
  };


function MyNote() {




    const [activeTabKey1, setActiveTabKey1] = useState('tab1');

    const onTab1Change = key => {
      setActiveTabKey1(key);
    };


  return (
    <div>
         <Card 

    hoverable
    style={{ width: 240 ,marginLeft:50 ,marginTop:50,borderRadius:30}}
    title="일주일통계"
    tabList={tabList}
    activeTabKey={activeTabKey1}
    onTabChange={key => {
      onTab1Change(key);
    }}
  >
      {contentList[activeTabKey1]}
        {/* <Chart></Chart> */}



  </Card>


    </div>
  )
}

export default MyNote
