import React from 'react'
import { Menu, Button,Row,Col} from 'antd';
import { useSelector } from 'react-redux';
import LoginForm from '../component/LoginForm';
import UserProfile from '../component/UserProfile';

import wrapper from '../store/configureStore';
import PostCard from '../component/PostCard';
import Guild from '../component/Guild';
function index() {
    const {me} =useSelector((state)=>state.user)
  return (
    <div> 
    <Row gutter={12}>
                {/* <Col span={12}>asd</Col>
                <Col span={12}>asds</Col> */}
                <Col xs={24} md={6}>
                    {me? <UserProfile></UserProfile>:<LoginForm></LoginForm>}
                </Col>
                <Col xs={24} md={10}><PostCard></PostCard></Col>
                <Col xs={24} md={6}><Guild></Guild></Col>

            </Row>
    </div>
  )
}

export default wrapper.withRedux(index);
