import React from 'react'
import { Menu, Button,Row,Col} from 'antd';
import { useSelector } from 'react-redux';
import LoginForm from '../component/LoginForm';
import UserProfile from '../component/UserProfile';

import wrapper from '../store/configureStore';
import PostCard from '../component/PostCard';
import Guild from '../component/Guild';
import AppLayout from '../component/AppLayout';
import MyNote from '../component/MyNote';
import PostForm from '../component/postForm';
function index() {
    const {me} =useSelector((state)=>state.user)
    const {mainPosts}=useSelector((state)=>state.post)
  return (
    <div style={{backgroundColor:'#F6F7FA'}}> 
    <AppLayout></AppLayout>
    <Row gutter={12} >
                {/* <Col span={12}>asd</Col>
                <Col span={12}>asds</Col> */}
                <Col span={6}>
                    <UserProfile></UserProfile>
                    {/* {me? <UserProfile></UserProfile>:<LoginForm></LoginForm>} */}
                    <MyNote></MyNote>
                    {/* <oneExerciseChart/> */}
                </Col>
                <Col span={12}>{me&&<PostForm></PostForm>}
                {mainPosts.map((post)=> <PostCard post={post} key={post.id}/> )}
                </Col>
                <Col span={6}><Guild></Guild></Col>

            </Row>
    </div>
  )
}

export default wrapper.withRedux(index);
