import React,{useCallback,useState}from 'react'
import {Card,Avatar,Button} from 'antd'
import { useDispatch, useSelector } from 'react-redux'
import {  logoutRequestAction, LOGOUT_REQUEST} from '../reducers/user';
import FollowModal from './FollowModal';

function UserProfile() {

    const [isModalVisible, setIsModalVisible] = useState(false);

    const showModal = () => {
      setIsModalVisible(true);
    };


    const dispatch=useDispatch();
    const {me,logOutLoading}=useSelector((state)=>state.user)


    const onLogoutForm=useCallback(()=>{

        dispatch({
            type:LOGOUT_REQUEST
        })
    },[])

    return (
        <div>
            <Card
            style={{height:300,width:220,marginTop:50,marginLeft:60,borderRadius:30}}
               actions={[
                   <button onClick={showModal} ><div key="twit">게시물수<br/>{me.Posts.length}<br/></div></button>

                ,
                <div key="following">팔로잉<br/>{me.Followings.length}</div>,

                <div key="follower">팔로워<br/>{me.Followers.length}</div>

            ]} 


            >
                <FollowModal visible={isModalVisible} setIsModalVisible={setIsModalVisible}></FollowModal>
                <Card.Meta
                avatar={<Avatar>{me.nickname[0]}</Avatar>}
                title={me.nickname}
                />

{/* <Button onClick={onLogoutForm} loading={logOutLoading} size='small' style={{marginLeft:35}}> 로그아웃</Button> */}





            </Card>


        </div>
    )
}

export default UserProfile
