import {Card,Table} from 'antd'
import { fa } from 'faker/lib/locales';

const dummyMap={
    id: "62133c5ff58346f2e09756ae",
    name: "복현동~영진전문대",
    distance: 350,
    userId: 1,
    description: "test",
    event: "bike",
    checkPoint: [
        [
