            ],
            [
                128.58748197555542,
                35.86634330488515
            ]
        ],
        id: "62133c5ff58346f2e09756af"
    },
    altitude: [
        { x: 0, y: 8 },
        { x: 1, y: 5 },
        { x: 2, y: 4 },
        { x: 3, y: 9 },
        { x: 4, y: 34 },
        { x: 5, y: 7 },
    ],
    start_latlng: [
        128.5076750946045,
        35.85643024981724
    ],
    end_latlng: [
        128.58748197555542,
        35.86634330488515
    ],
    createdAt: "2022-02-21T07:16:47.648Z",
    "__v": 0

}


const mapContainerStyle = {
    height: "400px",
    width: "800px",
    marginLeft:'50px',
    marginTop:'20px'
  };


function createPath() {


const [strokeWeight,setStrokeWeight]=useState(5)


const mouseOut=()=>{
    setStrokeWeight(5)
    console.log('aaa')
}

const mouseOver=()=>{
    setStrokeWeight(10)
    console.log('over')
}


    const options = {
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 5,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: false,
        draggable: true,
        editable: true,
        visible: true,
        radius: 30000,
        //-27부터 호주 시작해서 점찍고 점찍고 한다
        paths: [
              [
                  {lat:  35.969997373905, lng: 128.45170755523503},
                  {lat:35.985501427015464, lng:128.40407191943035},

                  {lat: 35.89580489690752, lng: 128.62238368221892}

              ]

        ],
        zIndex: 1
      };

      const drag=()=>{
          console.log('drag')
      }

  return (
    <div>
    <LoadScript
    googleMapsApiKey="AIzaSyAYsO2CGL0YCjMoLk29eyitFC2PIJnJbYE"
    >

      <GoogleMap
        id="marker-example"
        mapContainerStyle={mapContainerStyle}
        zoom={12}
        center={{lat:35.85669182451275, lng:  128.58788967132568}}




      >

    <Marker  position={{lat:dummyMap.start_latlng[1],lng:dummyMap.start_latlng[0]}}  />





         <Polyline onDragEnd={drag} onMouseOver={mouseOver} onMouseOut={mouseOut}  options={options} path={dummyMap.gps.coordinates.map((m)=>(
            {
                lat:m[1],
                lng:m[0]
            }
        ))}></Polyline> 







      </GoogleMap>
      </LoadScript></div>
  )
}

export default createPath
