                <Col  xl={12} xxl={12} >
                    {/* <PostForm></PostForm> */}
                {mainPosts.map((post)=> <PostCard post={post} key={post.id}/> )}
                </Col>
                <Col xl={6} xxl={6}>
                    {/* <Badge></Badge> */}
