import { Layout, Progress, Card } from "antd";
import { Logo } from "../../styles/styles";
import Link from "next/link";
import styled, { createGlobalStyle } from "styled-components";
import UserForm from "./UserForm";
import MenuMenu from "./Menu";
import React ,{useState} from 'react'

const { Sider } = Layout;
const { Meta } = Card;

const SideBar = () => {
    const [isSide, setIsSide]=useState(false)
    const show =()=>{
        setIsSide(!isSide)
    }
  return (
    <SiderWrapper
      width={220}
      collapsible
      collapsed={isSide}
      onCollapse={show}
    >
      <Global />
      <div
        style={{
          height: "32px",
          margin: "16px",
        }}
      >
        <div>
          <Logo>
            <SideLogo>
              <Link href="/">
                <a>
                  <img src="rider.png" />
                </a>
              </Link>
            </SideLogo>
            {!isSide && (
              <Link href="/" style={{ display: "none" }}>
                <a>
                  <p>Runnable</p>
                </a>
              </Link>
            )}
          </Logo>
        </div>
      </div>

      {isSide ? <SpaceDiv /> : <UserForm />}
      <MenuMenu />
      <div>
        {!isSide && (
          <Card hoverable style={{ width: 200 }}>
            <ProgressWrapper
              type="circle"
              strokeColor={{
                "0%": "#108ee9",
                "100%": "#87d068",
              }}
              percent={55}
            />
            <Meta title="운동 목표 달성까지" description="www.Runnable.com" />
          </Card>
        )}
      </div>
    </SiderWrapper>
  );
};

const ProgressWrapper = styled(Progress)`
  // 운동 목표
  position: relative;
  top: -10px;
  display: inline-block;
  width: 100%;
  text-align: center;
`;

const SpaceDiv = styled.div`
  // 빈공간
  height: 200px;
`;

const SiderWrapper = styled(Sider)`
  // 사이드바
  overflow: auto;
  position: sticky;
  top: 0;
  height: 100vh;
  z-index: 1;
`;

const Global = createGlobalStyle` // 전역 css
    .ant-layout-sider {
        background-color:white;
        // background-color: rgba( 255, 255, 255, 0.5 );
        border-right: 1px solid #f0f0f0;
    }
    .ant-layout-sider-trigger {
      background:#1890ff;
      // opacity:0.5;
    }

    .ant-menu-light .ant-menu-item:hover, .ant-menu-light .ant-menu-item-active {
      color:#1890ff;
    }
    .a:hover {
      color:#1890ff;
    }
    .slick-slide {
      display:inline-block;
    }
    .ant-card-cover {
      transform: none !important;
    }
    .ant-card-meta {
      text-align:center;
      & .ant-card-meta-title {
        font-weight:bold;
        margin-top:5px;
      }
    }
`;

const SideLogo = styled.div`
  // 로고
  display: inline-block;
  text-align: center;
  & img {
    width: 35px;
    heigh: 35px;
    overflow: hidden;
    white-space: nowrap;
  }
`;

export default SideBar;
