import React, {useState,useEffect} from 'react'
import { GoogleMap, LoadScript } from '@react-google-maps/api';
import { Polyline ,Marker,StreetViewPanorama,MarkerClusterer} from '@react-google-maps/api';
import AppLayout from '../component/AppLayout';
// import Streetview from 'react-google-streetview';// import RecordElevation from '../component/RecordElavation'

import GeomHandle from '../component/GoogleMap'
import { Input } from 'antd';
import { InfoWindow } from '@react-google-maps/api';
// import InfoWindow from '../component/InfoWindow';






const mapContainerStyle = {
  height: "400px",
  width: "800px"
};

const call=()=>{
    console.log('cccccc')
}

const center = {lat:  35.969997373905, lng: 128.45170755523503};

const onLoad = polyline => {
  console.log('polyline: ', polyline)
};



const path = [
[
    {lat:  35.969997373905, lng: 128.45170755523503},
    {lat:35.985501427015464, lng:128.40407191943035},

    {lat: 35.89580489690752, lng: 128.62238368221892}

]
,
[
    {lat:  35.24651100148735,   lng: 128.87070362130143},

    {lat: 35.24752656439432,   lng:128.8729993854958},
    {lat:35.248984786322424, lng:128.87318007064073},
    {lat:35.25018259185734, lng:128.87332887017186},
    {lat:35.25065129355367, lng:128.87505069331763},
    {
        lat:35.25145581526787, lng:128.87358573620605
    }


]




];

const markerPosition=[
    {lat: 35.89580489690752, lng: 128.62238368221892},{
        lat:35.25145581526787, lng:128.87358573620605
    }
]


function createKey(location) {
    return location.lat + location.lng
  }


const optionss = {
    imagePath:
      'https://developers.google.com/maps/documentation/javascript/examples/markerclusterer/m', // so you must have m1.png, m2.png, m3.png, m4.png, m5.png and m6.png in that folder
  }


function polyline() {


//InfoWindow
const [target,setTarget]=useState(false)

const divStyle = {
    background: `white`,
    border: `1px solid #ccc`,
    padding: 15
  }

const [infoPosition,setInfoPosition]=useState('')


//InfoWindow

    //polyline

const [strokeWeight,setStrokeWeight]=useState(5)

const mouseOut=()=>{
    setStrokeWeight(5)
    console.log('aaa')
}

const mouseOver=()=>{
    setStrokeWeight(10)
    console.log('over')
}
const polylineClick=(positionData)=>{
    console.log('click')
    setTarget(true)
    setInfoPosition(positionData)
}

const options = {
    strokeColor: '#FF0000',
    strokeOpacity: 0.8,
    strokeWeight: strokeWeight,
    fillColor: '#FF0000',
    fillOpacity: 0.35,
    clickable: true,
    draggable: false,
    editable: false,
    visible: true,
    radius: 30000,
    //-27부터 호주 시작해서 점찍고 점찍고 한다
    paths: [

          [
              {lat:  35.969997373905, lng: 128.45170755523503},
              {lat:35.985501427015464, lng:128.40407191943035},

              {lat: 35.89580489690752, lng: 128.62238368221892}

          ]
          ,
          [
              {lat:  35.24731788471166,  lng: 128.86906207420088},

              {lat: 35.260354548127424,  lng:128.81198466603703}

          ]



    ],
    zIndex: 1
  };



//polyline





    const [mapref, setMapRef] = useState(null);
    const handleOnLoad = map => {
      setMapRef(map);
      console.log('onLoad')
    };
    const handleCenterChanged = () => {
      if (mapref) {
        const zoom=mapref.getZoom()
        const lat = mapref.getCenter().lat();
        const lng = mapref.getCenter().lng();
        console.log('ddddddd',lat,lng,zoom);
      }
    };









    const myLatLng = { lat: -25.363, lng: 131.044 };
    // const map = new google.maps.Map(document.getElementById("map"), {
    //   zoom: 4,
    //   center: myLatLng,
    // });
    const [geom ,setGeom]=useState({lat:35.969997373905, lng: 128.45170755523503})

    const handleButton = async() => {
        const currentAddr = document.getElementById('address').value
        if (currentAddr){
          const {lat, lng} = await GeomHandle(currentAddr)
          setGeom({lat:lat,lng:lng})

        }
      }



    return (
        <div>
            <AppLayout></AppLayout>        
         검색하기
        <Input.Search id="address"  style={{verticalAlign:'middle' ,marginLeft:'10px',width:'500px'}} /><br></br>
        <button onClick={handleButton}>클릭</button>


        <LoadScript
        googleMapsApiKey="AIzaSyAYsO2CGL0YCjMoLk29eyitFC2PIJnJbYE"
        >

          <GoogleMap
            id="marker-example"
            mapContainerStyle={mapContainerStyle}
            zoom={14}
            center={geom}
            onLoad={handleOnLoad}
            onDragEnd={handleCenterChanged}
            onZoomChanged={handleCenterChanged}


          >

<MarkerClusterer options={optionss}>
          {(clusterer) =>
            markerPosition.map((p) => (
              <Marker key={createKey(p)} position={p} onClick={call} clusterer={clusterer} />
            ))
          }
        </MarkerClusterer>
        <Marker position={{lat:35.25018259185734, lng:128.87332887017186}}  ></Marker>
        {/* {path.map((p)=>{

        })} */}

        {path.map((m)=>(
            <Polyline onMouseOver={mouseOver} onMouseOut={mouseOut} onLoad={onLoad}  options={options} path={m}></Polyline>

            ))}
             {/* <StreetViewPanorama
      position={center}
      visible={true}
    /> */}
    {target&&<InfoWindow position={infoPosition}>
        <div style={divStyle}>
            <h1>동영</h1>
        </div>
        </InfoWindow>}





          </GoogleMap>
          </LoadScript>
               {/* <div className="chart">
           {graphData && (
             <RecordElevation graphData={graphData} setPosition={setPosition} />
           )}

         </div> */}
         {/* <Streetview address="경남김해시" APIkey="AIzaSyAYsO2CGL0YCjMoLk29eyitFC2PIJnJbYE" zoomLevel={15}/> */}
         </div>

    )
  }

  export default React.memo(polyline)
