  .ant-card {
    width: 100%;
    box-shadow: 0 1px 2px -2px rgb(0 0 0 / 16%), 0 3px 6px 0 rgb(0 0 0 / 12%);
  }

  .ant-timeline {
    position: relative;
    top: 40px;
    padding-left: 12px;
  }
`;

