import React,{useEffect,useState} from 'react'
import { GoogleMap, LoadScript } from '@react-google-maps/api';
import { Polyline ,Marker,StreetViewPanorama,MarkerClusterer} from '@react-google-maps/api';
import {Card,Table,Button,Slider, Switch,Row,Col} from 'antd'
import { fa } from 'faker/lib/locales';
import CreatedPath from './createdPath';
import SideBar from '../component/Layout/SideBar';



function createPath() {



const dummyMap={
    id:3,
name:'두류공원~동성로',
distance:350,
userId:1,
description:'설명',
event:'running',
gps:{
    type:"LineString",
    coordinates: [

        [128.61568699047842,
            35.89783218289432 
        ],
        [ 128.61388454612188,
            35.89831887973966
        ],
        [ 128.6200643553443,
            35.89567677518036
        ],
        [128.62714538674498,
            35.89696307394185 
        ],
        [128.62555751909755,
            35.892443553570914
        ],
        [128.62933406917793,
            35.89421662693352 
        ]

    ],
    id: "62133c5ff58346f2e09756af"

},
altitude: [
    { x: 0, y: 8 },
    { x: 1, y: 5 },
    { x: 2, y: 4 },
    { x: 3, y: 9 },
    { x: 4, y: 34 },
    { x: 5, y: 7 },
],

start_latlng: [
    128.62933406917793,
    35.89421662693352 
],
end_latlng: [
    128.61568699047842,
35.89783218289432 
],
createdAt: "2022-02-21T07:16:47.648Z",

}


const mapContainerStyle = {
    height: "400px",
    width: "800px",
    marginLeft:'50px',
    marginTop:'20px'
  };



const [strokeWeight,setStrokeWeight]=useState(5)


const mouseOut=()=>{
    setStrokeWeight(5)
    console.log('aaa')
}

const mouseOver=()=>{
    setStrokeWeight(10)
    console.log('over')
}


    const options = {
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 5,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: true,
        draggable: false,
        editable: false,
        visible: true,
        radius: 30000,
        //-27부터 호주 시작해서 점찍고 점찍고 한다
        paths: [
              [
                  {lat:  35.969997373905, lng: 128.45170755523503},
                  {lat:35.985501427015464, lng:128.40407191943035},

                  {lat: 35.89580489690752, lng: 128.62238368221892}

              ]

        ],
        zIndex: 1
      };

      const drag=()=>{
          console.log('drag')
      }

      const onLoad = polyline => {
        console.log('polyline: ', polyline)
      };

      const [number,setNumber]=useState([0,dummyMap.gps.coordinates.length-1])

      const [leftPath,setLeftPath]=useState({
          lat:dummyMap.start_latlng[1],lng:dummyMap.start_latlng[0]
        })
      const [rightPath,setRightPath]=useState({
        lat:dummyMap.end_latlng[1],lng:dummyMap.end_latlng[0]
      })

      const change=(value)=>{
        setNumber(value)

      }

      useEffect(()=>{
          setLeftPath({
              lat:dummyMap.gps.coordinates[number[0]][1],
              lng:dummyMap.gps.coordinates[number[0]][0]

            })
            console.log(number[0])

      },[number[0]])
      useEffect(()=>{
        setRightPath({
            lat:dummyMap.gps.coordinates[number[1]][1],
            lng:dummyMap.gps.coordinates[number[1]][0]

          })
          console.log(number[1])


    },[number[1]])
    const storePath=[]
    const [newPath,setNewPath]=useState([])

    const pathStore=()=>{

        for(var i=number[0]; i<number[1]+1; i++){
            storePath.push(dummyMap.gps.coordinates[i])
            console.log(storePath)
            setNewPath([...newPath,storePath])

        }
        // dummyMap.gps.coordinates=storePath
        console.log('aaa',newPath)
        console.log('qq',dummyMap.gps.coordinates)

        console.log('..',storePath)

    }

  return (
    <div>
        <Row>
            <Col span={3}>
                <SideBar></SideBar>
            </Col>
            <Col span={12}>
            <h1>{number[0]}</h1>
<h1>{number[1]}</h1>
        <Slider range defaultValue={number} max={dummyMap.gps.coordinates.length-1} onChange={change} />
        {/* {state&&<Switch size="small" checked={disabled} onChange={this.handleDisabledChange} />} */}
    <LoadScript
    googleMapsApiKey="AIzaSyAYsO2CGL0YCjMoLk29eyitFC2PIJnJbYE"
    >

      <GoogleMap
        id="marker-example"
        mapContainerStyle={mapContainerStyle}
        zoom={12}

        center={{lat:35.89567677518036, lng:  128.6200643553443}}




      >

    {/* <Marker  position={{lat:dummyMap.start_latlng[1],lng:dummyMap.start_latlng[0]}}  /> */}

    <Marker position={leftPath} icon={{url:' http://maps.google.com/mapfiles/ms/icons/blue.png',}} ></Marker>
    <Marker position={rightPath} ></Marker>


         <Polyline onDragStart={drag} onLoad={onLoad}  options={options} path={dummyMap.gps.coordinates.map((m)=>(
            {
                lat:m[1],
                lng:m[0]
            }
        ))}></Polyline> 







      </GoogleMap>
      </LoadScript>

      <Button onClick={pathStore}>경로만들기</Button>

    <div>
        <CreatedPath mapContainerStyle={mapContainerStyle} options={options} newPath={newPath} dummyMap={dummyMap}></CreatedPath>
        </div>  

            </Col>
        </Row>



{/* <Slider defaultValue={number} onChange={change} /> */}

    </div>
  )
}

export default createPath
