import produce from 'immer'

export const initialState={
    following:[
        {
            id:2,
            name:'재현'

        },
        {
            id:3,
            name:'현종'
        }
    ],
    follower:[
        {
            id:2,
            name:'재현'

        },
        {
            id:3,
            name:'현종'
        }
    ],
    me:null,
    logInLoading:false,
    LogInDone:false,
    logInError:null,
    logOutLoading:false,
    logOutDone:false,
    logOutError:null,
    followLoading: false,
    followDone: false,
    followError: null,
    unfollowLoading: false, // 언팔로우 시도중
    unfollowDone: false,
    unfollowError: null,
    signUpLoading: false, // 회원가입 시도중
    signUpDone: false,
    signUpError: null,
}



export const SIGNUP_SUCCESS='SIGNUP_SUCCESS'
export const SIGNUP_REQUEST='SIGNUP_REQUEST'
export const SIGNUP_FAIL='SIGNUP_FAIL'

export const LOGOUT_REQUEST='LOGOUT_REQUEST'
export const LOGOUT_SUCCESS='LOGOUT_SUCCESS'
export const LOGOUT_FAIL='LOGOUT_FAIL'


export const LOGIN_REQUEST='LOGIN_REQUEST'
export const LOGIN_SUCCESS='LOGIN_SUCCESS'
export const LOGIN_FAIL='LOGIN_FAIL'

export const FOLLOWING_REQUEST='FOLLOWING_REQUEST'
export const FOLLOWING_SUCCESS='FOLLOWING_SUCCESS'
export const FOLLOWING_FAIL='FOLLOWING_FAIL'

export const UNFOLLOWING_REQUEST='UNFOLLOWING_REQUEST'
export const UNFOLLOWING_SUCCESS='UNFOLLOWING_SUCCESS'
export const UNFOLLOWING_FAIL='UNFOLLOWING_FAIL'


const dummyUser=(data)=>(
    {
        ...data,
        nickname:'동영',
        id:1,
        Posts:[],
        Followings:[{nickname:'dong'},{nickname:'kim'},{nickname:'doeeng'}],
        Followers:[{nickname:'dongsss'},{nickname:'kimsss'},{nickname:'doeengsss'}],


    }
)

const reducer=(state=initialState,action)=>{
    return produce(state,(draft)=>{
        switch(action.type){
            case LOGIN_REQUEST:
                draft.logInLoading=true              
                break;
            case LOGIN_SUCCESS:
                draft.logInLoading=false
                draft.me=dummyUser(action.data)  
                // draft.me=action.data             
                break;
            case LOGIN_FAIL:
                draft.logInLoading=false
                break;
            case LOGOUT_REQUEST:
                draft.logOutLoading=true        
                break;
            case LOGOUT_SUCCESS:
                draft.logOutLoading=false
                draft.me=null
                break;
            case LOGOUT_FAIL:
                draft.logOutLoading=false
                break;
                    case FOLLOWING_REQUEST:
                        draft.followLoading = true;
                        draft.followError = null;
                        draft.followDone = false;
                        break;
                    case FOLLOWING_SUCCESS:
                        draft.followLoading = false;
                        draft.me.Followings.push({ id: action.data });
                        draft.followDone = true;
                        break;
                    case FOLLOWING_FAIL:
                        draft.followLoading = false;
                        draft.followError = action.error;
                        break;
                    case  UNFOLLOWING_REQUEST:
                        draft.unfollowLoading = true;
                        draft.unfollowError = null;
                        draft.unfollowDone = false;
                        break;
                    case  UNFOLLOWING_SUCCESS:
                        draft.unfollowLoading = false;
                        draft.me.Followings = draft.me.Followings.filter((v) => v.id !== action.data);
                        draft.unfollowDone = true;
                        break;
                    case  UNFOLLOWING_FAIL:
                        draft.unfollowLoading = false;
                        draft.unfollowError = action.error;
                        break;
                        case SIGNUP_REQUEST:
                            draft.signUpLoading = true;
                            draft.signUpError = null;
                            draft.signUpDone = false;
                            break;
                          case SIGNUP_SUCCESS:
                            draft.signUpLoading = false;
                            draft.signUpDone = true;
                            break;
                          case SIGNUP_FAIL:
                            draft.signUpLoading = false;
                            draft.signUpError = action.error;
                            break;
            default:
                return state
        }
    })
}

export default reducer
