import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { GoogleMap, LoadScript } from "@react-google-maps/api";
import {
  Polyline,
  Marker,
  StreetViewPanorama,
  MarkerClusterer,
} from "@react-google-maps/api";
import { InfoWindow } from "@react-google-maps/api";
import Router from "next/router";
import { LOAD_MAP_REQUEST } from "../../reducers/map";
import {
  MOVING_MAP_REQUEST,
  BIKE_MAP_REQUEST,
  RUNNING_MAP_REQUEST,
  SEARCH_MAP_REQUEST,
  LOAD_MY_LOCATION_REQUEST,
} from "../../reducers/map";
import { Input, Button, Row, Col, Card, Pagination } from "antd";
import GeomHandle from "../GoogleMap";
import SearchList from "../SearchList";
import Link from "next/link";
import styled from "styled-components";
import wrapper from "../../store/configureStore";
import { SearchOutlined } from "@ant-design/icons";

var lat = 1;
var lng = 2;

function selectMap({}) {
  const { myLocation } = useSelector((state) => state.map);

  const [geom, setGeom] = useState();
  const { Search } = Input;

  const [loadMap, setLoadMap] = useState(false);

  function getLocation() {
    if (navigator.geolocation) {
      // GPS를 지원하면
      navigator.geolocation.getCurrentPosition(
        function (position) {
          setGeom({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
          setLoadMap(true);
        },
        function (error) {
          console.error(error);
        },
        {
          enableHighAccuracy: false,
          maximumAge: 0,
          timeout: Infinity,
        }
      );
    } else {
      alert("GPS를 지원하지 않습니다");
    }
  }

  const [isState, setIsState] = useState(false);
  const [mapState, setMapState] = useState("B");

  const dispatch = useDispatch();

  // const mapContainerStyle = {
  // marginTop:60,
  // height: "720px",
  // width: "1000px"
  // };
  const mapContainerStyle = {
    // Google Map 스타일
    width: "88%",
    height: "85vh",
    // margin: "0 5%",
    borderRadius: "15px",
    position: "relative",
    left: "8%",
  };

  const { searchMap } = useSelector((state) => state.map);

  // const [geom ,setGeom]=useState({lat:35.969997373905, lng: 128.45170755523503})
  const [mapref, setMapRef] = useState(null);

  useEffect(() => {
    getLocation();
    if (mapref) {
      dispatch({
        type: LOAD_MY_LOCATION_REQUEST,
        data: {
          north: {
            lat: mapref.getBounds().getNorthEast().lat(),
            lng: mapref.getBounds().getNorthEast().lng(),
          },
          south: {
            lat: mapref.getBounds().getSouthWest().lat(),
            lng: mapref.getBounds().getSouthWest().lng(),
          },
          event: mapState,
        },
      });
    }
  }, [loadMap]);

  const handleOnLoad = (map) => {
    setMapRef(map);
    console.log("load됨");
  };

  const handleCenterChanged = () => {
    dispatch({
      type: MOVING_MAP_REQUEST,
      data: {
        north: {
          lat: mapref.getBounds().getNorthEast().lat(),
          lng: mapref.getBounds().getNorthEast().lng(),
        },
        south: {
          lat: mapref.getBounds().getSouthWest().lat(),
          lng: mapref.getBounds().getSouthWest().lng(),
        },
        event: mapState,
      },
    });
  };

  //////markercluseter

  const optionss = {
    imagePath:
      "https://developers.google.com/maps/documentation/javascript/examples/markerclusterer/m", // so you must have m1.png, m2.png, m3.png, m4.png, m5.png and m6.png in that folder
  };

  function createKey(location) {
    return location.lat + location.lng;
  }

  //////markercluseter

  ///주소검색

  const { searchmapLoading } = useSelector((state) => state.map);

  const handleButton = async () => {
    const currentAddr = document.getElementById("address").value;
    if (currentAddr) {
      const { lat, lng } = await GeomHandle(currentAddr);
      setGeom({ lat: lat, lng: lng });

      dispatch({
        type: SEARCH_MAP_REQUEST,
        data: {
          north: {
            lat: mapref.getBounds().getNorthEast().lat(),
            lng: mapref.getBounds().getNorthEast().lng(),
          },
          south: {
            lat: mapref.getBounds().getSouthWest().lat(),
            lng: mapref.getBounds().getSouthWest().lng(),
          },
          event: mapState,
        },
      });
      console.log("aaa", searchMap);
    }
  };

  //주소검색

  //polyline

  //   const [propsPosition,setPropsPosition]=useState()

  const [propsId, setPropsId] = useState();

  const [infoPosition, setInfoPosition] = useState({
    lat: "",
    lng: "",
  });

  const [target, setTarget] = useState(false);

  const [strokeWeight, setStrokeWeight] = useState(
    Array.from({ length: searchMap.length }, () => 3)
  );
  const strokeWeights = Array.from({ length: searchMap.length }, () => 3);

  const polylineClick = (positionData) => {
    setInfoPosition({
      lat: positionData.gps.coordinates[3][1],
      lng: positionData.gps.coordinates[3][0],
    });
    console.log("qqq", positionData);
    setPropsId(positionData._id);
    // setPropsPosition(positionData)
    setTarget(true);
  };

  function mouseOver(index) {
    strokeWeights[index] = 10;
    setStrokeWeight(strokeWeights);
  }

  function mouseOut(index) {
    strokeWeights[index] = 3;
    setStrokeWeight(strokeWeights);
  }

  //polyline

  //infoWindow
  const closeClick = () => {
    setTarget(false);
  };

  const divStyle = {
    background: `white`,
    border: `1px solid #ccc`,
    padding: 15,
  };

  const oneRoute = () => {
    dispatch({
      type: LOAD_MAP_REQUEST,
      data: propsId,
    });

    Router.push({
      pathname: "/Route/[id]",
      query: { id: propsId },
    });

    console.log("dong", searchMap);
  };

  //infoWindow

  //   const [mapState,setMapState]=useState()
  // const {mapState}=useSelector((state)=>state.map)
  const bikeSelectMap = () => {
    setMapState("B");
    setIsState(true);
  };

  const runningSelectMap = () => {
    setMapState("R");
    setIsState(true);
  };

  useEffect(() => {
    if (mapState === "B" && isState === true) {
      dispatch({
        type: BIKE_MAP_REQUEST,
        data: {
          event: mapState,
          north: {
            lat: mapref.getBounds().getNorthEast().lat(),
            lng: mapref.getBounds().getNorthEast().lng(),
          },
          south: {
            lat: mapref.getBounds().getSouthWest().lat(),
            lng: mapref.getBounds().getSouthWest().lng(),
          },
        },
      });
      setIsState(false);
    } else if (mapState === "R" && isState === true) {
      dispatch({
        type: RUNNING_MAP_REQUEST,
        data: {
          event: mapState,
          north: {
            lat: mapref.getBounds().getNorthEast().lat(),
            lng: mapref.getBounds().getNorthEast().lng(),
          },
          south: {
            lat: mapref.getBounds().getSouthWest().lat(),
            lng: mapref.getBounds().getSouthWest().lng(),
          },
        },
      });
      setIsState(false);
    }
  }, [mapState, isState]);

  const [btn, setBtn] = useState(false);

  return (
    <Container>
      <CardDiv>
        <Row>
          <Col span={12}>
            <LeftDiv>
              <Title>코스찾기</Title>
              <div>
                <Search
                  loading={searchmapLoading}
                  onPressEnter={handleButton}
                  placeholder="코스를 입력해주세요"
                  enterButton
                  // allowClear
                  icon={<SearchOutlined />}
                  id="address"
                  size="large"
                />
                <Buttons>
                  <Button className="btn1" onClick={bikeSelectMap}>
                    자전거
                  </Button>
                  <Button className="btn2" onClick={runningSelectMap}>
                    달리기
                  </Button>
                </Buttons>
              </div>
              {/* <RowDiv gutter={[0, 16]}> */}
              {searchMap.map((p, index) => (
                // <Col span={24}>
                <SearchList
                  setInfoPosition={setInfoPosition}
                  setPropsId={setPropsId}
                  setTarget={setTarget}
                  index={index}
                  setStrokeWeight={setStrokeWeight}
                  list={p}
                  key={p.id}
                ></SearchList>
                // </Col>
              ))}
              {/* </RowDiv> */}
              <Page
                defaultCurrent={1}
                defaultPageSize={3}
                // total={searchMap.lenth}
                total={10}
                // onChange={onChangePage}
              />
            </LeftDiv>
          </Col>

          <Col span={12}>
            <LoadScript googleMapsApiKey="AIzaSyAYsO2CGL0YCjMoLk29eyitFC2PIJnJbYE">
              <GoogleMap
                id="marker-example"
                mapContainerStyle={mapContainerStyle}
                zoom={13}
                center={geom}
                onLoad={handleOnLoad}
                onDragEnd={handleCenterChanged}
                // onZoomChanged={handleCenterChanged}
              >
                <MarkerClusterer options={optionss}>
                  {(clusterer) =>
                    searchMap.map((p) => (
                      <Marker
                        key={createKey(p)}
                        position={{
                          lat: p.start_latlng[1],
                          lng: p.start_latlng[0],
                        }}
                        clusterer={clusterer}
                        icon={{
                          scaledSize: new google.maps.Size(30, 30),
                        }}
                      />
                    ))
                  }
                </MarkerClusterer>

                {searchMap.map((m, index) => (
                  <Polyline
                    onRightClick={() => polylineClick(m)}
                    onMouseOver={() => mouseOver(index)}
                    onMouseOut={() => mouseOut(index)}
                    options={{
                      strokeWeight: strokeWeight[index],
                      clickable: true,
                      visible: true,
                      strokeColor: "#FF0000",
                      fillColor: "#FF0000",
                    }}
                    path={m.gps.coordinates.map((p) => ({
                      lat: p[1],
                      lng: p[0],
                    }))}
                  ></Polyline>
                ))}

                {target && (
                  <InfoWindow position={infoPosition} onCloseClick={closeClick}>
                    <div style={divStyle}>
                      <h1>동영</h1>
                      {/* <Link href={{
                        pathname:'/Route/[id]',
                        query: {id:propsId},
                    }}><a>상세보기</a></Link>
                     */}
                      <button onClick={oneRoute}>상세보기</button>
                    </div>
                  </InfoWindow>
                )}
              </GoogleMap>
            </LoadScript>
          </Col>
        </Row>
      </CardDiv>
    </Container>
  );
}

