  }

  Button:focus {
    background: #1683e8;
    color: #fff;
  }
`;

const CardStyle = styled(Card)`
  width: 400px;
  margin: 0 auto;
  border-radius: 15px;
  box-shadow: 0 1px 2px -2px rgb(0 0 0 / 16%), 0 3px 6px 0 rgb(0 0 0 / 12%);
  // 0 5px 12px 4px rgb(0 0 0 / 9%);
`;

const HeaderWrapper = styled(Header)`
  display: inline-block;
  width: 100%;
  height: 100px;
  background: #f0f2f5;
  //   opacity: 0.5;
  font-size: 51px;
  font-weight: bold;
  text-align: center;
  line-height: 100px;
  color: #1890ff;
  margin-top: 30px;
`;

const ContentWrapper = styled(Content)`
  height: 60vh;
  background: #f0f2f5;
`;
