import React ,{useState}from 'react'
import { Card ,Avatar,Image,Space} from 'antd';
import { createFromIconfontCN } from '@ant-design/icons';
import RunningChart from './RunningChart';
import BikeChart from './BikeChart'
import { Tabs } from 'antd';
import { AppleOutlined, AndroidOutlined } from '@ant-design/icons';
import { FaRunning } from 'react-icons/fa';
import { MdDirectionsBike } from 'react-icons/md';
import { Affix, Button } from 'antd';


const IconFont = createFromIconfontCN({
    scriptUrl: '//at.alicdn.com/t/font_8d5l8fzk5b87iudi.js',
  });

const bell=[
    {
        content:'뱃지를 획득 하였습니다',
        icon:'AndroidOutlined',
        date:'2022-02-18'
    },
    {
        content:'하트를 눌렀습니다',
        icon:'AndroidOutlined',
        date:'2022-02-18'
    },
    {
        content:'라이딩이 완료되었습니다',
        icon:'AndroidOutlined',
        date:'2022-02-18'
    }
]


function badge() {


  return (
    <div >
        <div style={{marginTop:50, marginLeft:50, position:'fixed'}}>
            라이딩알림
        {
            bell.map((b)=>(
                <Card style={{marginTop:10,borderRadius:20}}>
                                     <IconFont type="icon-bike" />

                    {b.content}
                    <a style={{marginLeft:10,fontSize:11}}>-{b.date}-</a>
                </Card>

            ))
            }
        </div>

         {/* <Card 


    hoverable
    style={{ width: 240 ,marginLeft:50 ,marginTop:50,borderRadius:30,position:'fixed'}}
    title="라이딩알림"
    bordered={false}


  >




  </Card>
   */}

    </div>
  )
}

export default badge
