const logInAPI=async(datas)=>{
    try{
        const res= await fetch("https://2yubi.shop/api/login", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            credentials: "include",
            body: JSON.stringify({
              email:datas.email,
              password:datas.password,
            }),
          });
          const data= await res.json()
        //   axios.defaults.headers.common['x-access-token']=data.message
        cookie.save('dongs',data.login_token, {
            path: "/",
          });
        //   cookie.save("refreshToken", refreshToken, {
        //     path: "/",
        //   });
          return data

    }catch(err){
        console.log(err)
    }
}


function* login(action){

try{
    const result =yield call(logInAPI,action.data)
console.log('userResult',result.headers)
yield put({
    type:LOGIN_SUCCESS,
    data:result
})
}catch(err){
    yield put({
        type:LOGIN_FAIL,
        data:err.response.data
    })
}
} 


function logOutAPI(data){
    return axios.post('http://3.35.239.14/api/logout')
}

function* logout(){
    try{
        yield call(logOutAPI)
        yield put({
            type:LOGOUT_SUCCESS,
        })
    }catch(err){
        yield put({
