import React,{useCallback,useState,useEffect} from 'react';
import {Button} from 'antd'
import PropTypes from 'prop-types'
import { useSelector,useDispatch } from 'react-redux';
import { FOLLOW_REQUEST,UNFOLLOW_REQUEST } from '../reducers/user';

function FollowButton({post}) {

  const {following, follower} = useSelector((state)=>state.user)

  const isFollowing=following.find((v)=>v.id===post.User.id)
  useEffect(()=>{
    console.log('ss',isFollowing)
  },[])
    const [isFollow,setIsFollow]=useState(false)
    const follow=useCallback(()=>{
        setIsFollow(!isFollow)
    })

  return (
    <div>
        {isFollowing?<Button style={{bottom:30,fontSize:15,position:'relative',left:460,bottom:60}} onClick={follow}>언팔로우</Button>:<Button style={{bottom:30,fontSize:15,position:'relative',left:460,bottom:60}} onClick={follow}>팔ㄴ로우</Button>}
        </div>
  )
}

export default FollowButton
