import React, { useState, useCallback,useEffect } from "react";
import { Layout, Card, Form, Input, Button, Modal } from "antd";
import Signup from "./SignUpTest";
import styled from "styled-components";
import Link from "next/link";
import useInput from "../hooks/useInput";
import axios from "axios";
import Router from 'next/router'
import { useDispatch ,useSelector} from "react-redux";
import { LOGIN_REQUEST } from "../reducers/user";

import { signIn, signOut, useSession } from "next-auth/react"; // 소셜로그인
import GoogleButton from "react-google-button"; // google 버튼
import { LOAD_POSTS_REQUEST } from "../reducers/post";

const { Header, Footer, Content } = Layout;
const { Meta } = Card;

const signin = () => {
  const dispatch = useDispatch();

  const {me} =useSelector((state)=>state.user)

  useEffect(()=>{
      if(me.user){
          dispatch({
              type:LOAD_POSTS_REQUEST
          })
          console.log('dongss')
          di
         Router.push(
             '/'
         )
      }
  },[me.user])

  const { data: session } = useSession(); // 소셜 로그인

  const [form] = Form.useForm();

  const [isModal, setIsModal] = useState(false);

  const showModal = () => {
    setIsModal(true);
  };

  const openModal = () => {
    setIsModal((prev) => !prev);
    console.log(isModal);
  };

  const [id, onChangeId] = useInput("");
  const [password, onChangePassword] = useInput("");

  const onSubmit = () => {
    let body = {
      email: id,
      password: password,
    };

    console.log(body);

    dispatch({
      type: LOGIN_REQUEST,
      data: body,
    });
  };

  return (
    <Layout>
      <HeaderWrapper>Runnable</HeaderWrapper>
      <ContentWrapper>
        <CardStyle>
          <MetaDiv>
            <Meta title="Runnable 로그인" />
          </MetaDiv>
          <FormDiv>
            <FormWrapper onFinish={onSubmit} form={form} size="large">
              <Form.Item>
                <Input
                  value={id}
                  onChange={onChangeId}
                  placeholder="이메일 또는 전화번호"
                  required
                />
              </Form.Item>
              <Form.Item>
                <Input.Password
                  value={password}
                  onChange={onChangePassword}
                  placeholder="비밀번호"
                  required
                />
              </Form.Item>
              {/* 코드추가 */}
              <BtnDiv>
                <LineDiv>
                  <button
                    type="button"
                    className="line_btn"
                    onClick={() => {
                      signIn("line");
                    }}
                  >
                    <img src="btn_base.png" />
                    LINE으로 시작하기
                  </button>
                </LineDiv>
                <GoogleDiv>
                  <GooBtn
                    type="light"
                    label="Google로 시작하기"
                    onClick={() => {
                      signIn("google");
                    }}
                  />
                </GoogleDiv>
                <Button type="default" htmlType="submit">
                  Signin
                </Button>
              </BtnDiv>
              {/*  */}
            </FormWrapper>
          </FormDiv>
          <QuestionDiv>
            <div style={{ marginBottom: "10px" }}>
              <Link href="/findId">
                <a>계정을 잊으셨나요?</a>
              </Link>
            </div>
            <Meta description="또는" />
          </QuestionDiv>
          <SignupBtn>
            <Button type="primary" onClick={showModal} ghost>
              새로운 계정 생성
            </Button>
            <Signup isModal={isModal} openModal={openModal} />
          </SignupBtn>
        </CardStyle>
      </ContentWrapper>
      <Footer style={{ height: "20vh", background: "#fff" }}></Footer>
    </Layout>
  );
};

