import React,{useCallback}from 'react'
import {Card,Avatar,Button} from 'antd'
import { useDispatch, useSelector } from 'react-redux'
import {  logoutRequestAction, LOGOUT_REQUEST} from '../reducers/user';

function UserProfile() {

    const dispatch=useDispatch();
    const {me,logOutLoading}=useSelector((state)=>state.user)


    const onLogoutForm=useCallback(()=>{

        dispatch({
            type:LOGOUT_REQUEST
        })
    },[])

    return (
        <div>
            <Card
            style={{height:300,width:200,marginTop:50,marginLeft:60}}
               actions={[
                <div key="twit">게시물수<br/>{me.Posts.length}<br/></div>,
                <div key="following">팔로잉<br/>{me.Followings.length}</div>,

                <div key="follower">팔로워<br/>{me.Followers.length}</div>

            ]}  

            >
                <Card.Meta
                avatar={<Avatar>{me.nickname[0]}</Avatar>}
                title={me.nickname}
                />

<Button onClick={onLogoutForm} loading={logOutLoading} size='small' style={{marginLeft:40}}> 로그아웃</Button>





            </Card>


        </div>
    )
}

export default UserProfile
