import { all,call,fork,put,takeLatest, take} from 'redux-saga/effects'

import { ADD_POST_FAILURE, ADD_POST_REQUEST, ADD_POST_SUCCESS, LOAD_POSTS_FAILURE, LOAD_POSTS_SUCCESS,LOAD_POSTS_REQUEST } from '../reducers/post'


function addPostAPI(data){
// return axios.post('/signup',data)
}

function* addPost(action){

    try{
        // const result=yield call(addPostAPI,action.data)
        console.log('ddddddddd')
          yield put({
              type:ADD_POST_SUCCESS,
              data:action.data
          })
      }catch(err){
          yield put({
              type:ADD_POST_FAILURE,
              error:err.response.data,

          })
      }
}


function loadPostsAPI(data){
    // return axios.post('/loadPosts',data)
    }


function* loadPosts(action){
    try{
        // const result = yield call(loadPostsAPI,action.data)
        yield put({
            type:LOAD_POSTS_SUCCESS,
            data:action.data
        })

    }catch(err){
        yield put({
            type:LOAD_POSTS_FAILURE,
            error:err.response.data,

        })

    }
}


function* watchAddPost(){
    yield takeLatest(ADD_POST_REQUEST,addPost)
}

function* watchLoadPosts(){
    yield takeLatest(LOAD_POSTS_REQUEST,loadPosts)
}

export default function* rootSaga(){

    yield all([
        fork(watchAddPost),
        fork(watchLoadPosts)

    ])

}
