import React,{useEffect,useState} from 'react'
import AppLayout from '../../component/AppLayout';
import { GoogleMap, LoadScript } from '@react-google-maps/api';
import { Polyline ,Marker,StreetViewPanorama,MarkerClusterer} from '@react-google-maps/api';

import {Card,Table} from 'antd'

import { useRouter } from 'next/router';



const mapContainerStyle = {
    height: "400px",
    width: "800px"
  };

const dummyLank=[
    {
        'userId':1,
        'userNickname':'동영',
        'record':'02:36:02',
        'speed':'27',
        'date':'2021년2월22일'
    },
    {
        'userId':2,
        'userNickname':'재현',
        'record':'02:38:57',
        'speed':'26',
        'date':'2021년2월22일'
    },
    {
        'userId':3,
        'userNickname':'현종',
        'record':'02:40:22',
        'speed':'25',
        'date':'2021년2월22일'
    },
    {
        'userId':4,
        'userNickname':'새별',
        'record':'02:52:11',
        'speed':'25',
        'date':'2021년2월22일'
    },
    {
        'userId':5,
        'userNickname':'대영',
        'record':'02:55:32',
        'speed':'24',
        'date':'2021년2월22일'
    },
    {
        'userId':6,
        'userNickname':'세준',
        'record':'02:58:06',
        'speed':'24',
        'date':'2021년2월22일'
    }
]



const data = [
    {
      key: '1',
      name: 'John Brown',
      age: 32,
      address: 'New York No. 1 Lake Park',
    },
    {
      key: '2',
      name: 'Jim Green',
      age: 42,
      address: 'London No. 1 Lake Park',
    },
    {
      key: '3',
      name: 'Joe Black',
      age: 32,
      address: 'Sidney No. 1 Lake Park',
    },
  ];

  const columns = [

    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
    },
    {
      title: 'Age',
      dataIndex: 'age',
      key: 'age',
    },
    {
      title: 'Address',
      dataIndex: 'address',
      key: 'address',
    },

  ];




function oneRoute({query}) {

    const router=useRouter()
    useEffect(()=>{
        console.log(router.query)
    },[])


const [strokeWeight,setStrokeWeight]=useState(5)


const mouseOut=()=>{
    setStrokeWeight(5)
    console.log('aaa')
}

const mouseOver=()=>{
    setStrokeWeight(10)
    console.log('over')
}


    const options = {
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: strokeWeight,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: true,
        draggable: false,
        editable: false,
        visible: true,
        radius: 30000,
        //-27부터 호주 시작해서 점찍고 점찍고 한다
        paths: [
              [
                  {lat:  35.969997373905, lng: 128.45170755523503},
                  {lat:35.985501427015464, lng:128.40407191943035},

                  {lat: 35.89580489690752, lng: 128.62238368221892}

              ]

        ],
        zIndex: 1
      };

      const path=[
        {lat:  35.969997373905, lng: 128.45170755523503},
        {lat:35.985501427015464, lng:128.40407191943035},

        {lat: 35.89580489690752, lng: 128.62238368221892}
      ]

  return (
    <div>
    <AppLayout></AppLayout>        



<LoadScript
googleMapsApiKey="AIzaSyAYsO2CGL0YCjMoLk29eyitFC2PIJnJbYE"
>

  <GoogleMap
    id="marker-example"
    mapContainerStyle={mapContainerStyle}
    zoom={14}
    center={{lat:35.969997373905, lng: 128.45170755523503}}




  >

<Marker  position={path[path.length-1]}  />



    <Polyline onMouseOver={mouseOver} onMouseOut={mouseOut}  options={options} path={path}></Polyline>







  </GoogleMap>
  </LoadScript>

  <div >
          <div style={{marginBottom:10,marginLeft:50}}>순위</div>
        <div style={{ height:400,marginTop:20, marginLeft:50,width:800,overflow:'auto'}}>
            <Card style={{ height:50,height:50,width:780,borderRadius:30}}>
            <span style={{position:'absolute',bottom:12,left:20}}>순위</span>

<span style={{position:'absolute',bottom:12,left:50}}>이름</span>
<span style={{position:'absolute',bottom:12,left:125}}>속도</span>
<span style={{position:'absolute',bottom:12,left:220}}>기록</span>

<span style={{position:'absolute',bottom:12,left:370}}>날짜</span>
            </Card>

        {
            dummyLank.map((b,index)=>(
                <Card style={{marginBottom:10,borderRadius:30}}>
                                     <a>{index+1+'위'}</a>

                    <span style={{marginLeft:10}}>{b.userNickname}</span>
                    <span style={{marginLeft:50,top:10,fontSize:11}}>{b.speed}</span>
                    <span style={{marginLeft:80,top:10,fontSize:11}}>{b.record}</span>

                    <span style={{marginLeft:100,top:10,fontSize:11}}>{b.date}</span>
                </Card>

            ))
            }
        </div>


    </div>


    <Table columns={columns} dataSource={data} />




 </div>

 )
}

export default oneRoute
