import React ,{useState}from 'react'
import { Card } from 'antd';
import RunningChart from './RunningChart';
import BikeChart from './BikeChart'
import { Tabs } from 'antd';
import { AppleOutlined, AndroidOutlined } from '@ant-design/icons';
import { FaRunning } from 'react-icons/fa';
import { MdDirectionsBike } from 'react-icons/md';
import { Affix, Button } from 'antd';


const { TabPane } = Tabs;

import LoginForm from './LoginForm';
import dynamic from "next/dynamic";

const { Meta } = Card;
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });





function myPurpose() {
    const pie = {
        // 파이 차트 더미데이터
        series: [55, 20],
        chartOptions: {
          labels: ["러닝", "라이딩"],
          textAnchor: "middle",
          legend: {
            show: true,
            position: "bottom",
            horizontalAlign: "center",
          },
          plotOptions: {
            pie: {
              donut: {
                size: "40%",
              },
            },
          },
          colors: ["white", "#BBDEFB"],
          // "#1890ff"
        },
      };


  return (
    <div >
        {/* <div style={{position:'fixed',marginTop:30,marginLeft:55}}>일주일통계</div> */}
        <Card style={{width:300,borderRadius:30}}>
        <Chart
              type="donut"
              options={pie.chartOptions}
              series={pie.series}
              width={250}
              height={280}
            />


                </Card>



    </div>
  )
}

export default myPurpose
