import React, { useState } from "react";
import { Layout, Menu, Breadcrumb } from "antd";
import {} from "@ant-design/icons";
import SideBar from "./SideBar";
import { createGlobalStyle } from "styled-components";
import Header from "./Header";

const { Content, Footer } = Layout;

const AppLayout = ({ children }) => {
  const [isSide, setIsSide] = useState(false);

  const showSide = () => {
    setIsSide((prev) => !prev);
  };
  return (
    <div style={{ minHeight: "100vh" }}>
      <Layout>
        <SideBar isSide={!isSide} showSide={showSide} />
        <Layout style={{ backgroundColor: "rgba( 255, 255, 255, 0.5 )" }}>
          <Header />
          <Breadcrumb style={{ padding: "16px 15px" }}>
            <Breadcrumb.Item>localhost : 3000</Breadcrumb.Item>
            <Breadcrumb.Item>SNS</Breadcrumb.Item>
            <Breadcrumb.Item>Home</Breadcrumb.Item>
          </Breadcrumb>
          <Content
            style={{
              padding: 24,
              margin: "0 15px 15px 15px",
              height: "78vh",
              border: "solid 1px grey",
              backgroundColor: "#fff",
            }}
          >
            {children}
          </Content>
          {/* <Footer
            style={{
              textAlign: "center",
              height: "20vh",
              backgroundColor: "black",
              color: "#fff",
            }}
          >
            Ant Design Â©2018 Created by Ant UED
          </Footer> */}
        </Layout>
      </Layout>
    </div>
  );
};

export default AppLayout;
