        icon:'AndroidOutlined',
        date:'2022-02-18'
    }
]


function badge() {


  return (
    <div >
        <div style={{marginTop:50, marginLeft:50, position:'fixed'}}>
            라이딩알림
        {
            bell.map((b)=>(
                <Card style={{marginTop:10,borderRadius:20}}>{b.content}
