import { all,call,fork,put,takeLatest, take} from 'redux-saga/effects'
import {LOGIN_FAIL, LOGIN_REQUEST,LOGIN_SUCCESS,LOGOUT_REQUEST, LOGOUT_SUCCESS,LOGOUT_FAIL, SIGNUP_REQUEST,SIGNUP_SUCCESS,SIGNUP_FAIL} from '../reducers/user'
import axios from 'axios'

function logInAPI(data){
    // return axios.post('/login',data)
}

function* login(action){
// const result =yield call(logInAPI,action.data)
try{
yield put({
    type:LOGIN_SUCCESS,
    data:action.data
})
}catch(err){
    yield({
        type:LOGIN_FAIL,
        data:'error'
    })
}
} 


function logOutAPI(data){
    // return axios.post('/logout',data)
}

function* logout(){
    try{
        // yield call(logOutAPI)
        yield put({
            type:LOGOUT_SUCCESS,
        })
    }catch(err){
        yield put({
            type:LOGOUT_FAIL,
            error:'error'

        })
    }
} 

function signupAPI(data){
// return axios.post('/signup',data)
}

function* signup(action){

    try{
        // const result=yield call(signupAPI,action.data)

          yield put({
              type:SIGNUP_SUCCESS,
          })
      }catch(err){
          yield put({
              type:SIGNUP_FAIL,
              error:err.response.data,

          })
      }
}

function* watchLogin(){
    yield takeLatest(LOGIN_REQUEST,login)
}

function* watchLogout(){
    yield takeLatest(LOGOUT_REQUEST,logout)
}
function* watchSignup(){
    yield takeLatest(SIGNUP_REQUEST,signup)
}

export default function* rootSaga(){

    yield all([
        fork(watchLogin),
        fork(watchLogout),
        fork(watchSignup),
    ])

}
